#include <iostream>#include <cstring>using namespace std;int main(){	char str1[] = "Talha", str2[] = "Jubayer", str3[] = "Talha";    int result;    result = strcmp(str1, str2);    cout<<"strcmp(str1, str2) = "<<result<<endl;    result = strcmp(str1, str3);    cout<<"strcmp(str1, str3) = "<<result<<endl;    result = strcmp(str2, str3);    cout<<"strcmp(str2, str3) = "<<result<<endl;    cout<<"\nstrlen(str1) = "<<strlen(str1)<<endl;    cout<<"strlen(str2) = "<<strlen(str2)<<endl;	cout<<"strlen(str3) = "<<strlen(str3)<<endl;	return 0;}