#include <iostream>using namespace std;int main(){	int n;	cout<<"Enter the number: ";	cin>>n;	int arr[n];	for(int i=0;i<n;i++){		cout<<"Enter "<<i+1<<"th number: ";		cin>>arr[i];	}	int sum = 0;	for(int i=0;i<n;i++) sum+=arr[i];	cout<<"The average is: "<<(sum/n)<<endl;	return 0;}