#include <iostream>using namespace std;int main(){	int n;	cout<<"Enter the number: ";	cin>>n;	int arr[n];	for(int i=0;i<n;i++){		cout<<"Enter "<<i+1<<"th number: ";		cin>>arr[i];	}	int minimum = arr[0];	int maximum = arr[0];	for(int i=1;i<n;i++){		if(arr[i]<minimum) minimum = arr[i];		else if(arr[i]>maximum) maximum = arr[i];	}	cout<<"Minimum number: "<<minimum<<endl;	cout<<"Maximum number: "<<maximum<<endl;	return 0;}